generated.css:
.login-container{
    width: 100%;
    height: 100vh;
    font-family: 'Poppins', sans-serif;
}

.login-container h1{
    font-family: 'Righteous', cursive;
}

.login-container form{
    width: 400px;
    height: 250px;
}

.login-container input{
    outline: none;
    width: 90%;
    margin: 10px 0;
    border: none;
    border-radius: 5px;
}

.login-container input:last-child{
    background-color: rgba(187, 12, 231, 0.404);
    box-shadow: 10px 10px 38px -9px rgba(240,118,240,1);
}

.login-container input:not(:last-child){
    color: white;
    padding: 10px 10px;
    background-color: rgb(37, 37, 37);
    box-shadow: 10px 10px 38px -9px rgba(196,196,196,1);
}

.login-container span{
    font-size: 10px;  
    margin-top: 10px;
    cursor: pointer;
    transition: 300ms;
}

.login-container span:hover{
    color: blue;
}

.g-signin-button {
  /* This is where you control how the button looks. Be creative! */
  display: inline-block;
  padding: 4px 8px;
  border-radius: 3px;
  background-color: #3c82f7;
  color: #fff;
  box-shadow: 0 3px 0 #0f69ff;
  cursor: pointer;
}
